import React, { useCallback, useEffect, useState } from "react";

import { RadialBarChart, RadialBar, Legend, ResponsiveContainer, PolarAngleAxis, LabelList } from 'recharts';

import { getDataPerformanceById } from "../../../services/API/user";

import style from './scoreGraph.module.css';

interface Props {
    score: number,
}

function labelContent(score:any) {
    return (
        <>
        <div className={style.label}>
                <p className={style.percentage}>{score}%</p>
                <p className={style.text}>De votre objectif</p>
            </div>
        </>
    )
}

export const ScoreGraph: React.FC<Props> = ({ score }) => {
    console.log(score);
    const data = [{ score: score*100 }];
    const widthContainer = 258;
    const heightContainer = 263;

    if (score) {
        return (
            <>
                <div className={style.scoreGraph}>
                    <p className={style.title}>Score</p>
                    <RadialBarChart
                        startAngle={90}
                        innerRadius="80%"
                        outerRadius="100%"
                        barSize={10}
                        data={data}
                        width={widthContainer - 20}
                        height={heightContainer - 24}
                    >
                        <PolarAngleAxis
                            type="number"
                            domain={[0, 100]}
                            angleAxisId={0}
                            tick={false}
                        />
                        <RadialBar
                            dataKey="score"
                            fill="#FF0000"
                        >
                            <LabelList
                                dataKey="score"
                                position="center"
                                content={labelContent}
                            />
                        </RadialBar>
                    </RadialBarChart>

                </div>
            </>
        )
    } else {
        return (
            <>
                <h1>ERROR</h1>
            </>
        )
    }
}
