import React, { useEffect, useState } from "react";

import { BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Label } from 'recharts';
import { getDataWeightById } from "../../../services/API/user";

// css 
import style from './activityGraph.module.css';

interface Props {
    id: string,
}

const CustomTooltip = ({ active, payload }: any) => {
    if (active && payload && payload.length) {
    console.log(payload[0]);
    return (
        <div className="customTooltip">
          <p className="kilogram">{payload[0].value}Kg</p>
          <p className="calories">{payload[1].value}Kcal</p>
        </div>
      );
    }
  
    return null;
  };

const tooltipStyle = {
    background: '#E60000',
    textAlign: 'center',
    padding: '4px 7px 4px 6px',
}

export const ActivityGraph: React.FC<Props> = ({ id }) => {
    const [data, setData] = useState<IUserActivity>();
    useEffect(() => {
        if (id) {
            getDataWeightById(id).then((result) => {
                setData(result.data);
            }).catch(() => console.error('APPEL API ERROR'));
        }
    }, [id])
    if (data) {
        let dataGraph = [];
        for (let i = 0; i < data.sessions.length; i++) { dataGraph.push(data.sessions[i]) }
        return (
            <>
                <div className={style.containerGraph}>
                    <BarChart
                        width={835}
                        height={320}
                        data={dataGraph}
                        barSize={20}
                    >
                        <CartesianGrid
                            strokeDasharray="3 3"
                            vertical={false}
                        />
                        <XAxis
                            dataKey={0}
                            tickLine={false}
                        />
                        <YAxis
                            orientation="right"
                            tickLine={false}
                            axisLine={false}
                            tickCount={3}
                            tickMargin={45}
                        />
                        <Tooltip 
                        />
                        <Legend
                            align="right"
                            verticalAlign="top"
                            iconSize={8}
                            iconType="circle"
                            className={style.legend}
                            height={84}
                        />
                        <Bar 
                            dataKey="kilogram" 
                            fill="#282D30" 
                            name="Poids (kg)" 
                        />
                        <Bar 
                            dataKey="calories" 
                            fill="#E60000" 
                            name="Calories brûlées (kCal)" 
                        />
                    </BarChart>
                </div>
            </>
        );
    } else {
        return (
            <>
                <h1>ERROR</h1>
            </>
        )
    }
}
