import React, { useCallback, useEffect, useState } from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

import { getDataAverageSessionsById } from "../../../services/API/user";

import style from './averageSessionsGraph.module.css';

interface Props {
    id: string,
}

const CustomTooltip = ({ active, payload }: any) => {
    if (active && payload && payload.length) {
        return (
            <div className={style.customTooltip}>
                <p>{payload[0].payload.sessionLength} min</p>
            </div>
        );
    }

    return null;
};

export const AverageSessionsGraph: React.FC<Props> = ({ id }) => {
    const [data, setData] = useState<IUserActivity>();
    const [percGradient, setPercGradient] = useState(0);

    const getLetterOfDay = useCallback((number: number) => {
        switch (number) {
            case 1: return 'L';
            case 2: return 'M';
            case 3: return 'M';
            case 4: return 'J';
            case 5: return 'V';
            case 6: return 'S';
            case 7: return 'D';
        }
        return 'Error';
    }, [])

    const test = useCallback((hoveredData: any) => {
        console.log('data : ', hoveredData);
        /*if(hoveredData && hoveredData.isTooltipActive) {
            const percentage = (hoveredData.chartX / 253) * 100
            setPercGradient(percentage);
        }*/
        if (hoveredData && hoveredData.activePayload) {
            const hoveredX = hoveredData.activePayload[0].payload.name;
            const percentage = ((data.sessions.length - hoveredData.activeTooltipIndex - 1) * 100) / (data.sessions.length - 1);
      
            setPerc(100 - percentage);
          }
    }, [])

    useEffect(() => {
        if (id) {
            getDataAverageSessionsById(id).then((result) => {
                setData(result.data);
            }).catch(() => console.error('APPEL API ERROR'));
        }
    }, [id])
    if (data) {
        return (
            <>
                <div className={style.averageSessionsGraph}>
                    <ResponsiveContainer width="100%" height="100%">
                        <LineChart
                            width={253}
                            height={263}
                            data={data.sessions}
                            onMouseMove={test}
                        >
                            <defs>
                                <linearGradient id="colorUv" x1="0%" y1="0" x2="100%" y2="0">
                                    <stop offset="0%" stopColor="rgba(255, 255, 255, .4)" />
                                    <stop offset={`${percGradient}%`} stopColor="rgba(255, 255, 255, .4)" />
                                    <stop offset={`${percGradient}%`} stopColor="rgba(255, 255, 255, 1)" />
                                    <stop offset={'100%'} stopColor="rgba(255, 255, 255, 1)" />
                                </linearGradient>
                            </defs>
                            <Line
                                type="natural"
                                dataKey="sessionLength"
                                strokeWidth={3}
                                stroke="url(#colorUv)"
                            />
                            <XAxis
                                tickFormatter={getLetterOfDay}
                                tickLine={false}
                                axisLine={false}
                                dataKey="day"
                                stroke="rgba(255, 255, 255, 1)"
                                fontSize={12}
                                tickMargin={19}
                            />
                            <YAxis
                                hide={true}
                                domain={['dataMin-5', 'dataMax + 5']}
                            />
                            <Tooltip
                                wrapperStyle={{ backgroundColor: "red" }}
                                labelStyle={{ display: "none" }}
                                itemStyle={{ color: "black", fontSize: "12px" }}
                                formatter={(value: number) => ["", `${value} min`]}
                                separator=""
                            />

                        </LineChart>
                    </ResponsiveContainer>
                </div>
            </>
        )
    } else {
        return (
            <>
                <h1>ERROR</h1>
            </>
        )
    }
}
