import React, { useEffect, useState } from "react";

import { BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Label } from 'recharts';
import { getDataWeightById } from "../../../services/API/user";

// css 
import style from './activityGraph.module.css';

interface Props {
    id: string,
}

const CustomTooltip = ({ active, payload }: any) => {
    if (active && payload && payload.length) {
        return (
        <div className={style.customTooltip}>
          <p className={style.kilogram}>{`${payload[0].value}kg`}</p>
          <p className={style.calories}>{`${payload[1].value}Kcal`}</p>
        </div>
      );
    }
  
    return null;
  };

  const renderLegend = (value: string, entry: any) => {
    const { color } = entry;
    
    if(value==="Poids (kg)") {
        return <span className={style.kiloLegend} style={{ color }}>{value}</span>
    }
    return <span className={style.itemLegend} style={{ color }}>{value}</span>;
  };

export const ActivityGraph: React.FC<Props> = ({ id }) => {
    const [data, setData] = useState<IUserActivity>();
    useEffect(() => {
        if (id) {
            getDataWeightById(id).then((result) => {
                setData(result.data);
            }).catch(() => console.error('APPEL API ERROR'));
        }
    }, [id])
    if (data) {
        return (
            <>
                <div className={style.containerGraph}>
                    <ResponsiveContainer width="100%" height="100%">
                        <BarChart
                            data={data.sessions}
                            margin={{
                                top: 5,
                                right: 30,
                                left: 20,
                                bottom: 5,
                            }}
                            barSize={7}
                            barCategoryGap={26}
                            barGap={8}
                            
                        >
                            <XAxis
                                dataKey={0}
                                padding={{ left: 10, right: 10 }}
                                tickLine={false}
                                tickMargin={16}
                            />
                            <YAxis
                                tickCount={3}
                                tickLine={false}
                                axisLine={false}
                                orientation="right"
                                tickMargin={45}
                            />
                            <Tooltip
                                content={<CustomTooltip />}
                            />
                            <Legend 
                                className={style.legend}
                                align="right"
                                verticalAlign="top"
                                iconSize={8}
                                iconType="circle"
                                wrapperStyle={{
                                    top: '-48px'
                                }}
                                formatter={renderLegend}
                                
                            />
                            <CartesianGrid 
                                strokeDasharray="3 3" 
                                vertical={false}
                            />
                            <Bar name="Poids (kg)" dataKey="kilogram" fill="#282D30" />
                            <Bar name="Calories brûlées (kCal)" dataKey="calories" fill="#E60000" />
                        </BarChart>
                    </ResponsiveContainer>
                </div>
            </>
        );
    } else {
        return (
            <>
                <h1>ERROR</h1>
            </>
        )
    }
}
