import React, { useCallback, useEffect, useState } from "react";
import { Radar, RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis } from 'recharts';

import { getDataPerformanceById } from "../../../services/API/user";

import style from './performanceGraph.module.css';

interface Props {
    id: string,
}

export const PerformanceGraph: React.FC<Props> = ({ id }) => {
    const [data, setData] = useState<IUserPerformance>();

    const widthContainer = 258;

    const tradLabel = useCallback((label: string) => {
        switch (label) {
            case 'cardio': return 'Cardio';
            case 'energy': return 'Energie';
            case 'endurance': return 'Endurance';
            case 'strength': return 'Force';
            case 'speed': return 'Vitesse';
            case 'intensity': return 'Intensité';
        }
        return 'Error';
    }, [])

    useEffect(() => {
        if (id) {
            getDataPerformanceById(id).then((result) => {
                setData(result.data);
            }).catch(() => console.error('API ERROR'));
        }
    }, [id])

    if (data) {
        return (
            <>
                <RadarChart outerRadius={90} width={730} height={250} data={data.data}>
                    <PolarGrid />
                    <PolarAngleAxis dataKey="subject" />
                    <PolarRadiusAxis angle={30} domain={[0, 150]} />
                    <Radar name="Mike" dataKey="A" stroke="#8884d8" fill="#8884d8" fillOpacity={0.6} />
                    <Radar name="Lily" dataKey="B" stroke="#82ca9d" fill="#82ca9d" fillOpacity={0.6} />
                </RadarChart>
            </>
        )
    } else {
        return (
            <>
                <h1>ERROR</h1>
            </>
        )
    }
}
