import React, { useEffect, useState } from "react";

import { BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Label } from 'recharts';
import { getDataWeightById } from "../../../services/API/user";

// css 
import style from './activityGraph.module.css';

interface Props {
    id: string,
}

export const ActivityGraph: React.FC<Props> = ({ id }) => {
    const [data, setData] = useState<IUserActivity>();
    useEffect(() => {
        if (id) {
            getDataWeightById(id).then((result) => {
                setData(result.data);
            }).catch(() => console.error('APPEL API ERROR'));
        }
    }, [id])
    if (data) {
        return (
            <>
                <div className={style.containerGraph}>
                        <BarChart
                            width={835}
                            height={320}
                            data={data.sessions}
                            margin={{
                                top: 5,
                                right: 30,
                                left: 20,
                                bottom: 5,
                            }}
                            barSize={7}
                        >
                            <XAxis dataKey={0} scale="point" padding={{ left: 10, right: 10 }} tickLine={false} />
                            <YAxis tickCount={3} tickLine={false} axisLine={false}/>
                            <Tooltip />
                            <Legend />
                            <CartesianGrid strokeDasharray="3 3"/>
                            <Bar dataKey="kilogram" fill="#8884d8"/>
                            <Bar dataKey="calories" fill="#8884d8"/>
                        </BarChart>
                </div>
            </>
        );
    } else {
        return (
            <>
                <h1>ERROR</h1>
            </>
        )
    }
}
