import React, { useCallback, useEffect, useState } from "react";

import { RadialBarChart, RadialBar, Legend, ResponsiveContainer, PolarAngleAxis } from 'recharts';

import { getDataPerformanceById } from "../../../services/API/user";

import style from './scoreGraph.module.css';

interface Props {
    score: number,
}

export const ScoreGraph: React.FC<Props> = ({ score }) => {
    const data = [{ score: score }];
    const widthContainer = 258;
    const heightContainer = 263;

    if (score) {
        return (
            <>
                <div className={style.scoreGraph}>
                    <RadialBarChart
                        innerRadius="80%"
                        outerRadius="100%"
                        barSize={10}
                        data={data}
                        width={widthContainer}
                        height={heightContainer}
                    >
                         <PolarAngleAxis
                        type="number"
                        domain={[0, 10]}
                        angleAxisId={0}
                        tick={false}
                    />
                        <RadialBar
                            dataKey="score"
                            fill="#FF0000"
                        />
                    </RadialBarChart>
                   
                </div>
            </>
        )
    } else {
        return (
            <>
                <h1>ERROR</h1>
            </>
        )
    }
}
