import React, { useCallback, useEffect, useState } from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

import { getDataAverageSessionsById } from "../../../services/API/user";


interface Props {
    id: string,
}

export const AverageSessions: React.FC<Props> = ({ id }) => {
    const [data, setData] = useState<IUserActivity>();

    useEffect(() => {
        if (id) {
            getDataAverageSessionsById(id).then((result) => {
                setData(result.data);
            }).catch(() => console.error('APPEL API ERROR'));
        }
    }, [id])
    if (data) {
        return (
            <>
                <ResponsiveContainer width="100%" height="100%">
                    <LineChart
                        width={500}
                        height={300}
                        data={data.sessions}
                        margin={{
                            top: 5,
                            right: 30,
                            left: 20,
                            bottom: 5,
                        }}
                    >
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="name" />
                        <YAxis />
                        <Tooltip />
                        <Legend />
                        <Line type="monotone" dataKey="pv" stroke="#8884d8" activeDot={{ r: 8 }} />
                        <Line type="monotone" dataKey="uv" stroke="#82ca9d" />
                    </LineChart>
                </ResponsiveContainer>
            </>
        );
    } else {
        return (
            <>
                <h1>ERROR</h1>
            </>
        )
    }
}
