import React, { useEffect, useState } from "react";

import { BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { getDataWeightById } from "../../../services/API/user";

// css 
import style from './activityGraph.module.css';

interface Props {
    id: string,
}

export const ActivityGraph: React.FC<Props> = ({ id }) => {
    const [data, setData] = useState<IUserActivity>();
    useEffect(() => {
        if (id) {
            getDataWeightById(id).then((result) => {
                setData(result.data);
            }).catch(() => console.error('APPEL API ERROR'));
        }
    }, [id])
    if (data) {
        let dataGraph = [];
        for (let i = 0; i < data.sessions.length; i++) { dataGraph.push(data.sessions[i]) }
        return (
            <>
                <BarChart 
                    width={730}
                    height={250}
                    data={dataGraph}
                    barSize={7}
                    barCategoryGap={54}
                    barGap={8}
                >
                    <CartesianGrid 
                        strokeDasharray="3 3"
                        vertical={false} 
                    />
                    <XAxis 
                        dataKey={0} 
                        tickLine={false}
                    />
                    <YAxis 
                        orientation="right"
                        tickLine={false}
                        fill='white'
                    />
                    <Tooltip />
                    <Legend
                        align="right"
                        verticalAlign="top"
                        iconSize={8}
                        iconType="circle"
                    />
                    <Bar className={style.kilogram} dataKey="kilogram" fill="#282D30" name="Poids (kg)"/>
                    <Bar className={style.calories} dataKey="calories" fill="#E60000" name="Calories brûlées (kCal)"/>
                </BarChart>
            </>
        );
    } else {
        return (
            <>
            <h1>ERROR</h1>
            </>
        )
    }
}
