import React, { useCallback, useEffect, useState } from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

import { getDataAverageSessionsById } from "../../../services/API/user";

import style from './averageSessionsGraph.module.css';

interface Props {
    id: string,
}

const CustomTooltip = ({ active, payload }: any) => {
    if (active && payload && payload.length) {
        console.log(payload[0].payload.sessionLength);
        return (
            <div className={style.customTooltip}>
                <p>{payload[0].payload.sessionLength}</p>
            </div>
        );
    }

    return null;
};

export const AverageSessionsGraph: React.FC<Props> = ({ id }) => {
    const [data, setData] = useState<IUserActivity>();

    const getLetterOfDay = useCallback((number: number) => {
        switch (number) {
            case 1: return 'L';
            case 2: return 'M';
            case 3: return 'M';
            case 4: return 'J';
            case 5: return 'V';
            case 6: return 'S';
            case 7: return 'D';
        }
        return 'Error';
    }, [])

    useEffect(() => {
        if (id) {
            getDataAverageSessionsById(id).then((result) => {
                setData(result.data);
            }).catch(() => console.error('APPEL API ERROR'));
        }
    }, [id])
    if (data) {
        return (
            <>
                <div className={style.averageSessionsGraph}>
                    <ResponsiveContainer width="100%" height="100%">
                        <LineChart
                            width={253}
                            height={263}
                            data={data.sessions}
                        >
                            <XAxis
                                tickFormatter={getLetterOfDay}
                                tickLine={false}
                                axisLine={false}
                                dataKey="day"
                                stroke="rgba(255, 255, 255, 1)"
                                fontSize={12}
                                tickMargin={19}
                            />
                            <YAxis 
                                hide={true}
                                domain={['dataMin-5', 'dataMax + 5']}
                            />
                            <Tooltip 
                                content={<CustomTooltip />}

                            />
                            <Line 
                                type="natural" 
                                dataKey="sessionLength" 
                                stroke="rgba(255, 255, 255)" 
                            />
                        </LineChart>
                    </ResponsiveContainer>
                </div>
            </>
        )
    } else {
        return (
            <>
                <h1>ERROR</h1>
            </>
        )
    }
}
