import React, { useEffect, useState } from "react";

import { BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Label } from 'recharts';
import { getDataWeightById } from "../../../services/API/user";

// css 
import style from './activityGraph.module.css';

interface Props {
    id: string,
}

export const ActivityGraph: React.FC<Props> = ({ id }) => {
    const [data, setData] = useState<IUserActivity>();
    useEffect(() => {
        if (id) {
            getDataWeightById(id).then((result) => {
                setData(result.data);
            }).catch(() => console.error('APPEL API ERROR'));
        }
    }, [id])
    if (data) {
        let dataGraph = [];
        for (let i = 0; i < data.sessions.length; i++) { dataGraph.push(data.sessions[i]) }
        return (
            <>
                <div className={style.containerGraph}>
                    <BarChart
                        width={835}
                        height={320}
                        data={dataGraph}
                        barGap={8}
                        barSize={7}
                        margin={{top: 24, right: 29.5, bottom: 23, left: 32}}
                    >
                        <CartesianGrid
                            strokeDasharray="3 3"
                            vertical={false}
                        />
                        <XAxis
                            dataKey={0}
                            tickLine={false}
                        />
                        <YAxis
                            orientation="right"
                            tickLine={false}
                            axisLine={false}
                            tickCount={3}
                            tickMargin={45}
                        />
                        <Tooltip />
                        <Legend
                            align="right"
                            verticalAlign="top"
                            iconSize={8}
                            iconType="circle"
                            height={30}
                        />
                        <Bar className={style.kilogram} dataKey="kilogram" fill="#282D30" name="Poids (kg)" />
                        <Bar className={style.calories} dataKey="calories" fill="#E60000" name="Calories brûlées (kCal)" />
                    </BarChart>
                </div>
            </>
        );
    } else {
        return (
            <>
                <h1>ERROR</h1>
            </>
        )
    }
}
