import React, { useCallback, useEffect, useState } from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

import { getDataAverageSessionsById } from "../../../services/API/user";

import style from './averageSessionsGraph.module.css';

interface Props {
    id: string,
}

export const AverageSessionsGraph: React.FC<Props> = ({ id }) => {
    const [data, setData] = useState<IUserActivity>();

    const getLetterOfDay = useCallback((number: number) => {
        switch (number) {
            case 1: return 'L';
            case 2: return 'M';
            case 3: return 'M';
            case 4: return 'J';
            case 5: return 'V';
            case 6: return 'S';
            case 7: return 'D';
        }
        return 'Error';
    }, [])

    useEffect(() => {
        if (id) {
            getDataAverageSessionsById(id).then((result) => {
                setData(result.data);
            }).catch(() => console.error('APPEL API ERROR'));
        }
    }, [id])
    if (data) {
        return (
            <>
                <div className={style.averageSessionsGraph}>
                    <ResponsiveContainer width="100%" height="100%">
                        <LineChart
                            width={253}
                            height={263}
                            data={data.sessions}
                            margin={{
                                top: 5,
                                right: 30,
                                left: 20,
                                bottom: 5,
                            }}
                        >
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis
                                tickFormatter={getLetterOfDay}
                                tickLine={false}
                                dataKey="day"
                            />
                            <YAxis 
                                hide={true}
                                domain={['dataMin-5', 'dataMax + 5']}
                            />
                            <Tooltip />
                            <Legend />
                            <Line type="basis" dataKey="sessionLength" stroke="#FFFFFF" />
                        </LineChart>
                    </ResponsiveContainer>
                </div>
            </>
        )
    } else {
        return (
            <>
                <h1>ERROR</h1>
            </>
        )
    }
}
