import React, { useState } from "react";
import { ActivityGraph } from "../graph/barChart/ActivityGraph";
import { AverageSessionsGraph } from "../graph/averageSessionsGraph/averageSessionsGraph";
import { PerformanceGraph } from "../graph/performanceGraph/PerformanceGraph";
import { ScoreGraph } from "../graph/scoreGraph/ScoreGraph";
import { ContainerData } from "../containerData/ContainerData";
// css 
import style from './dashboard.module.css';

// images 
import fireSVG from '../../images/fire.svg';
import thighSVG from '../../images/thigh.svg';
import appleSVG from '../../images/apple.svg';
import cheeseburgerSVG from '../../images/cheeseburger.svg';

interface Props {
    data: IUser,
}

export const Dashboard: React.FC<Props> = ({ data }) => {
    const paramContainerData = [
        { name: 'Calories', value: data.keyData.calorieCount, unite: 'kCal', color: '#FF0000' },
        { name: 'Proteines', value: data.keyData.proteinCount, unite: 'g', color: '#4AB8FF' },
        { name: 'Glucides', value: data.keyData.carbohydrateCount, unite: 'g', color: '#FDCC0C' },
        { name: 'Lipides', value: data.keyData.lipidCount, unite: 'g', color: '#FD5181' },
        ]
    return (
        <>
            <div className={style.dashboard}>
                <h1>Bonjour {data.userInfos.firstName}</h1>
                <p>F√©licitation ! Vous avez explos√© vos objectifs hier üëè</p>
                <div className={style.line}>
                    <div className={style.graph}>
                        <ActivityGraph id={data.id.toString()} />
                        <div className={style.line}>
                            <AverageSessionsGraph id={data.id.toString()} />
                            <PerformanceGraph id={data.id.toString()} />
                            {data.todayScore &&
                                <ScoreGraph score={data.todayScore} />
                            }
                            {data.score &&
                                <ScoreGraph score={data.score} />
                            }
                        </div>
                    </div>
                    <div className={style.containerData}>
                        {paramContainerData.map((param, key) => (
                            <ContainerData key={key} param={param} keyImage={key} />
                        ))
                        }
                    </div>
                </div>
            </div>
        </>
    );
}
